DROP TABLE drug;
CREATE TABLE drug(
   drugID       VARCHAR(13) NOT NULL
  ,drugname     VARCHAR(30) NOT NULL
  ,manufacturer VARCHAR(30) NOT NULL
  ,dosage       NUMBER(4,0) NOT NULL
  ,cost         NUMBER(5,2) NOT NULL
);


DROP TABLE doctor;
CREATE TABLE doctor(
   doctorID      NUMBER(2,0) NOT NULL
  ,forename      VARCHAR(20) NOT NULL
  ,surname       VARCHAR(30) NOT NULL
  ,Street        VARCHAR(30) NOT NULL
  ,town          VARCHAR(20) NOT NULL
  ,postcode      VARCHAR(8) NOT NULL
  ,telNo         VARCHAR(13) NOT NULL
  ,dateQualified DATE  NOT NULL
);




ALTER SESSION SET NLS_DATE_FORMAT='DD-MON-YYYY HH24:MI:SS';

DROP TABLE appointment;
CREATE TABLE appointment(
   appDate   DATE  NOT NULL
  ,appTime   DATE NOT NULL
  ,doctorID  INTEGER  NOT NULL
  ,patientID INTEGER  NOT NULL
);



DROP TABLE prescribedDrug;
CREATE TABLE prescribedDrug(
   patientID      INTEGER  NOT NULL
  ,drugID         VARCHAR(13) NOT NULL
  ,datePrescribed DATE  NOT NULL
);


DROP TABLE patient;
CREATE TABLE patient(
   patientID   NUMBER(3,0) NOT NULL
  ,forename    VARCHAR(20) NOT NULL
  ,surname     VARCHAR(20) NOT NULL
  ,dateOfBirth DATE  NOT NULL
  ,Address     VARCHAR(70) NOT NULL
  ,contactNo   VARCHAR(13) NOT NULL
);
